[["男频","1"],["女频","0"],["出版","2"]].map([title,id]=>{
	push('🔖'+title+'榜单🔖', null, 1, 1);
	if(id==2){ 	$$=$$.slice(1,$$.length-1) }
		$$.map([title,uri]=>{			
		  	url= `/api/novel/channel/homepage/rank/rank_list/v2/?aid=13&limit=30&offset={{(page-1)*30}}&side_type=${uri}&type=${id}`;
				push(title, url, 1, 0.25);			
			});
	});

