data=JSON.parse(java.ajax(category_url)).data;

  function json(data) {
     boy=data.boy_category;
     girl=data.girl_category;
     publish=data.publish_category;
     json=[["男频",boy],["女频",girl],["出版",publish]];
     return JSON.parse(JSON.stringify(json));
   }	

		json(data).map([tit1,category]=>{
	[["连载","1"],["完结","0"]].map([tit2,id]=>{  
	    push('🔖'+tit1+'🍁'+tit2+'🔖', null, 1, 1);
     category.map(($,index)=>{
     	index++;
	    title=$.category_name;
     	cid=$.category_id;
	url= `/api/novel/channel/homepage/new_category/book_list/v1/?aid=1967&limit=10&offset={{(page-1)*10}\}&category_id=${cid}&creation_status=${id}&gender=1`;
  	if(index <= category.length - category.length%3)
			 {	push(title, url, 1, 0.25);}
				else{ push(title, url, 0, 0.3);}
          });
      });
  });
