@js:
function spArr(arr, num){
  newArr = []
	for( i = 0; i < arr.length;){
		newArr.push(arr.slice(i, i += num))
	}
	return newArr
}

res = JSON.parse(result).data
item_list = spArr(res["item_list"], 100)

array = []
for( i = 0; i < item_list.length; i ++){
	  response = java.ajax('https://api-lf.fanqiesdk.com/api/novel/book/directory/detail/v1/?item_ids='+item_list[i]);
	   data = JSON.parse(response).data;  
		data.forEach($=> {				
		 text= $.title;
	   href='/api/novel/book/reader/full/v1/?device_platform=android&aid=2329&item_id='+$.item_id;
	  info=((Number($.volume_name)==0?"第一卷：默认":$.volume_name)+' 📍 '+java.timeFormatUTC($.first_pass_time*1000,'yyyy-MM-dd HH:mm',0)+' 📍'+$.chapter_word_number+'字'); 

		array.push({
			text: text,
			href: href,
			info: info	
	})
	})			
}

